        // 이미지 목록 로드 함수
        function loadImages(folder, callback) {
            var xhr = new XMLHttpRequest();
            xhr.open('GET', pluginPath + 'get-stickers.php?folder=' + folder, true); // get 경로 get-stickers_From@Common.php OR get-stickers.php
            xhr.onload = function () {
                if (xhr.status === 200) {
                    try {
                        var images = JSON.parse(xhr.responseText);
                        callback(images);
                    } catch (e) {
                        console.error('Failed to parse images list:', e);
                        callback([]);
                    }
                } else {
                    console.error('Failed to load images:', xhr.status);
                    callback([]);
                }
            };
            xhr.onerror = function () {
                console.error('Network error while loading images');
                callback([]);
            };
            xhr.send();
        }

        // 이미지 표시 함수
        function displayImages(images, gridDiv, imagePath) {
            if (!gridDiv) {
                console.error('Grid div not found');
                return;
            }

            gridDiv.innerHTML = '';

            if (images.length === 0) {
                gridDiv.innerHTML = '<p style="text-align:center; padding:20px; color:#666;">' +
                    editor.lang.stickers.noStickers + '</p>';
                return;
            }

            // 그리드 스타일 적용
            gridDiv.style.display = 'grid';
            gridDiv.style.gridTemplateColumns = 'repeat(auto-fill, minmax(100px, 1fr))';
            gridDiv.style.gap = '10px';
            gridDiv.style.padding = '12px';
            gridDiv.style.maxHeight = '350px';
            gridDiv.style.overflowY = 'auto';
            gridDiv.style.overflowX = 'hidden';

            // 각 이미지 추가
            images.forEach(function (image) {
                var imageWrapper = document.createElement('div');
                imageWrapper.style.cursor = 'pointer';
                imageWrapper.style.border = '2px solid transparent';
                imageWrapper.style.borderRadius = '8px';
                imageWrapper.style.padding = '4px';
                imageWrapper.style.transition = 'all 0.3s ease';
                imageWrapper.style.textAlign = 'center';
                imageWrapper.style.backgroundColor = 'transparent';

                var img = document.createElement('img');
                img.src = imagePath + image;
                img.alt = image;
                img.style.maxWidth = '100%';
                img.style.height = 'auto';
                img.style.display = 'block';
                img.style.margin = '0 auto';
                img.style.borderRadius = '4px';

                // 마우스 오버 이벤트
                imageWrapper.onmouseover = function () {
                    this.style.border = '2px solid #0078D7';
                    this.style.backgroundColor = '#f0f8ff';
                    this.style.transform = 'scale(1.05)';
                    this.style.boxShadow = '0 4px 8px rgba(0,0,0,0.1)';
                };

                imageWrapper.onmouseout = function () {
                    this.style.border = '2px solid transparent';
                    this.style.backgroundColor = 'transparent';
                    this.style.transform = 'scale(1)';
                    this.style.boxShadow = 'none';
                };

                // 클릭 이벤트 - 에디터에 이미지 삽입
                imageWrapper.onclick = function () {
                    var imgElement = editor.document.createElement('img');
                    imgElement.setAttribute('src', imagePath + image);
                    imgElement.setAttribute('alt', image.replace(/\.[^/.]+$/, ''));
                    imgElement.setAttribute('class', 'inserted-sticker');

                    editor.insertElement(imgElement);

                    // 다이얼로그 닫기
                    CKEDITOR.dialog.getCurrent().hide();
                };

                imageWrapper.appendChild(img);
                gridDiv.appendChild(imageWrapper);
            });
        }

        // 이모지 표시 함수
        function displayEmojis(gridDiv) {
            if (!gridDiv) {
                console.error('Emoji grid div not found');
                return;
            }

            gridDiv.innerHTML = '';
            gridDiv.style.padding = '15px';
            gridDiv.style.maxHeight = '350px';
            gridDiv.style.overflowY = 'auto';
            gridDiv.style.overflowX = 'hidden';

            // 각 카테고리별로 이모지 표시
            for (var category in emojiList) {
                // 카테고리 제목
                var categoryTitle = document.createElement('h3');
                categoryTitle.textContent = category;
                categoryTitle.style.fontSize = '14px';
                categoryTitle.style.fontWeight = 'bold';
                categoryTitle.style.margin = '15px 0 10px 0';
                categoryTitle.style.color = '#333';
                categoryTitle.style.borderBottom = '2px solid #0078D7';
                categoryTitle.style.paddingBottom = '5px';
                gridDiv.appendChild(categoryTitle);

                // 이모지 그리드 컨테이너
                var emojiContainer = document.createElement('div');
                emojiContainer.style.display = 'grid';
                emojiContainer.style.gridTemplateColumns = 'repeat(auto-fill, minmax(50px, 1fr))';
                emojiContainer.style.gap = '5px';
                emojiContainer.style.marginBottom = '10px';

                // 각 이모지 추가
                emojiList[category].forEach(function (emoji) {
                    var emojiWrapper = document.createElement('div');
                    emojiWrapper.textContent = emoji;
                    emojiWrapper.style.cursor = 'pointer';
                    emojiWrapper.style.fontSize = '32px';
                    emojiWrapper.style.textAlign = 'center';
                    emojiWrapper.style.padding = '8px';
                    emojiWrapper.style.border = '2px solid transparent';
                    emojiWrapper.style.borderRadius = '8px';
                    emojiWrapper.style.transition = 'all 0.3s ease';
                    emojiWrapper.style.backgroundColor = 'transparent';

                    // 마우스 오버 이벤트
                    emojiWrapper.onmouseover = function () {
                        this.style.border = '2px solid #0078D7';
                        this.style.backgroundColor = '#f0f8ff';
                        this.style.transform = 'scale(1.2)';
                        this.style.boxShadow = '0 4px 8px rgba(0,0,0,0.1)';
                    };

                    emojiWrapper.onmouseout = function () {
                        this.style.border = '2px solid transparent';
                        this.style.backgroundColor = 'transparent';
                        this.style.transform = 'scale(1)';
                        this.style.boxShadow = 'none';
                    };

                    // 클릭 이벤트 - 에디터에 이모지 삽입
                    emojiWrapper.onclick = function () {
                        var emojiText = this.textContent;
                        editor.insertText(emojiText);
                        CKEDITOR.dialog.getCurrent().hide();
                    };

                    emojiContainer.appendChild(emojiWrapper);
                });

                gridDiv.appendChild(emojiContainer);
            }
        }

